mapper.select:
select * from fix_data
 1=1

        and billdate = #{billdate}


        and billdate >= #{billdateStart}


        and billdate <= #{billdateEnd}


        and deptid = #{deptid}


        and udfcode = #{udfcode}


        and erp_num  = #{erpNum}


        and diff_num  = #{diffNum}









        and status  = #{status}


        and line = #{line}

    order by line

select * from report
where 1=1

        and status = #{status}

mapper.update:
update report set

        status = #{status}


        ,price = #{price}


        ,price1 = #{price1}


        ,price2 = #{price2}

where line = #{line}

mapper.select:
select * from fix_data_xm
where 1=1

        and status = #{status}
